INSERT INTO `films` VALUES (85, 'Arsenic and Old Lace', '1944-09-23', 'http://image.tmdb.org/t/p/w185/81BZ372hnaGIzgb825Ny2hoxxDO.jpg', 4.0, 'Mortimer Brewster is a newspaperman and author known for his diatribes against marriage. We watch him being married at city hall in the opening scene. Now all that is required is a quick trip home to tell Mortimer\'s two maiden aunts. While trying to break the news, he finds out his aunts\' hobby; killing lonely old men and burying them in the cellar. It gets worse.', 'Frank Capra', 'Cary Grant,Priscilla Lane,Josephine Hull,Jean Adair,Raymond Massey,Peter Lorre,John Alexander,Jack Carson,James Gleason,Edward Everett Horton,Grant Mitchell,Edward McNamara,Garry Owen,John Ridgely,Vaughan Glaser,Chester Clute,Charles Lane,Edward McWade,Spencer Charters,Hank Mann,Jimmy the Crow,Sol Gorss,Spec O\'Donnell,Lee Phelps,Raymond Walburn,Leo White', 'Comedy,Crime', 'http://image.tmdb.org/t/p/w780/gb8pPJ3214jUql3RuZdwJ4NJvPS.jpg');
